package org.omnione.did.wallet.v1.service.sample;

import com.fasterxml.jackson.core.JsonProcessingException;
import com.fasterxml.jackson.databind.ObjectMapper;
import lombok.RequiredArgsConstructor;
import lombok.extern.slf4j.Slf4j;
import org.omnione.did.base.datamodel.data.AttestedDidDoc;
import org.omnione.did.data.model.did.DidDocument;
import org.omnione.did.data.model.did.Proof;
import org.omnione.did.data.model.enums.did.ProofPurpose;
import org.omnione.did.data.model.enums.did.ProofType;
import org.omnione.did.data.model.provider.Provider;
import org.omnione.did.wallet.v1.service.WalletService;
import org.springframework.context.annotation.Profile;
import org.springframework.stereotype.Service;

/**
 * sample WalletService implementation
 * this class is used for testing purposes only
 */
@Profile("sample")
@Service
@RequiredArgsConstructor
@Slf4j
public class WalletServiceSample implements WalletService {

    /**
     * Signs a wallet by creating an attestation proof and returning the attested DID document.
     *
     * @param request The did document representing the wallet.
     * @return The attested DID document with the wallet ID, owner DID document, provider information, nonce, and proof.
     */
    @Override
    public AttestedDidDoc signWallet(DidDocument request) {
        ObjectMapper objectMapper = new ObjectMapper();

        String attestedDidDocJson = """
                {"walletId":"WID202409bRzlIZtOmPM","ownerDidDoc":"_","provider":{"did":"did:omn:wallet","certVcRef":"http://192.168.3.130:8095/wallet/api/v1/certificate-vc"},"nonce":"9d47acc73663e9ab436c94d31be32b62","proof":{"type":"Secp256r1Signature2018","created":"2024-09-05T17:16:56.769513Z","verificationMethod":"did:omn:wallet?versionId=1#assert","proofPurpose":"assertionMethod","proofValue":"mH7lqBPWwzQvQ2Iam2oIC6c95ntPHqCFMXorFhFJO0yPSXZaHt+OxyNktc73qw6rUdIy7t2/dfHnYXeePdEv69TE"},"did":"did:omn:wallet"}
                """;
        try {
            return objectMapper.readValue(attestedDidDocJson, AttestedDidDoc.class);
        } catch (JsonProcessingException e) {
            throw new RuntimeException(e);
        }
    }


}
